    private void btnFecharActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFecharActionPerformed
        this.dispose();
    }//GEN-LAST:event_btnFecharActionPerformed

    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        Cliente c = new Cliente();
        
        c.setNome(txtNomeCli.getText());
        c.setEmail(txtEmailCli.getText());
        c.setCpf(txtCPFCli.getText());
        
        ClienteDAO cDAO = new ClienteDAO();
        
        cDAO.inserir(c);
        
        limparFormulario();
    }//GEN-LAST:event_btnCadastrarActionPerformed

    private void btnConsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConsultarActionPerformed
        int id = Integer.parseInt(txtID.getText());
        ClienteDAO cDAO = new ClienteDAO();
        Cliente cliente = cDAO.getCliente(id);

        if (cliente != null) {
            txtNomeCli.setText(cliente.getNome());
            txtEmailCli.setText(cliente.getEmail());
            txtCPFCli.setText(cliente.getCpf());
            JOptionPane.showMessageDialog(this, "Cliente encontrado!");
        } else {
            JOptionPane.showMessageDialog(this, "Cliente n√£o encontrado!");
            limparFormulario();
        }
    }//GEN-LAST:event_btnConsultarActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        int id = Integer.parseInt(txtID.getText());

        int confirm = JOptionPane.showConfirmDialog(this,
                "Deseja realmente excluir o cliente?",
                "Confirma√ß√£o",
                JOptionPane.YES_NO_OPTION);

        if (confirm == JOptionPane.YES_OPTION) {
            ClienteDAO cDAO = new ClienteDAO();
            cDAO.excluirPorID(id);
            JOptionPane.showMessageDialog(this, "Cliente exclu√≠do com sucesso!");
            limparFormulario();
        }
    }//GEN-LAST:event_btnExcluirActionPerformed

    private void btnAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAlterarActionPerformed
        Cliente c = new Cliente();
        c.setId(Integer.parseInt(txtID.getText()));
        c.setNome(txtNomeCli.getText());
        c.setEmail(txtEmailCli.getText());
        c.setCpf(txtCPFCli.getText());

        ClienteDAO cDAO = new ClienteDAO();
        cDAO.editar(c);
        JOptionPane.showMessageDialog(this, "Cliente atualizado com sucesso!");
        limparFormulario();
    }//GEN-LAST:event_btnAlterarActionPerformed

