    private void btnSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarActionPerformed
        if(rdoMasc.isSelected()){
            sexo = "M";
            JOptionPane.showMessageDialog(null, "Sexo selecionado: " + sexo, "Alerta", JOptionPane.OK_OPTION);
        }else if(rdoFem.isSelected()){
            sexo = "F";
            JOptionPane.showMessageDialog(null, "Sexo selecionado: " + sexo, "Alerta", JOptionPane.OK_OPTION);
        }else{
            JOptionPane.showMessageDialog(null, "Selecione um sexo\n" + sexo, "Erro", JOptionPane.ERROR_MESSAGE);
        }
        //Verifica interesses
        if(chkTecnologia.isSelected())
            tecnologia = true;
        else
            tecnologia = false;
        
        if(chkAstronomia.isSelected())
            astronomia = true;
        else
            astronomia = false;
        
        if(chkEsportes.isSelected())
            esportes = true;
        else
            esportes = false;
        
        JOptionPane.showMessageDialog(null, "Tecnologia: " + tecnologia + "\nAstronomia: " + astronomia + "\nEsportes: " + esportes, sexo, HEIGHT);
        
        Pessoa p = new Pessoa(txtNome.getText(), sexo, (String) cmbIdioma.getSelectedItem());
        
        DefaultTableModel tabela = (DefaultTableModel) tbl_Pessoas.getModel();
        tabela.addRow(p.obterDados());
    }//GEN-LAST:event_btnSalvarActionPerformed

